import React from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCube, faRobot, faAtom } from '@fortawesome/free-solid-svg-icons';

const ResearchSection = styled.section`
  background: var(--background-dark1);
`;

const SectionTitle = styled.h2`
  text-align: center;
  font-size: 2.5rem;
  margin-bottom: 60px;
  position: relative;
  padding-bottom: 15px;

  &::after {
    content: '';
    position: absolute;
    width: 60px;
    height: 3px;
    background: var(--primary-color);
    bottom: 0;
    left: 50%;
    transform: translateX(-50%);
  }
`;

const CardsContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 40px;
`;

const Card = styled.div`
  background: var(--background-dark2);
  padding: 40px;
  border-radius: 8px;
  text-align: center;
  transition: transform 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.2);
  }
`;

const IconWrapper = styled.div`
  font-size: 3rem;
  color: var(--primary-color);
  margin-bottom: 20px;
`;

const CardTitle = styled.h3`
  font-size: 1.5rem;
  margin-bottom: 15px;
`;

const CardText = styled.p`
  color: var(--text-color-muted);
  line-height: 1.7;
`;

const researchData = [
  {
    icon: faCube,
    title: 'Finite Element Method',
    text: 'Ansys를 활용한 정밀 유한요소해석(FEM)을 통해 복잡한 공학 문제의 구조적, 열적 거동을 분석하고 예측합니다.',
  },
  {
    icon: faRobot,
    title: '로봇 공학',
    text: '차세대 로봇 플랫폼을 설계하고, 인간과 상호작용하는 로봇을 위한 제어 알고리즘을 연구합니다.',
  },
  {
    icon: faAtom,
    title: '나노 기술',
    text: '나노 스케일에서의 재료 특성을 연구하고, 이를 응용한 초소형 정밀 부품 및 센서 기술을 개발합니다.',
  },
];

function Research() {
  return (
    <ResearchSection id="research">
      <div className="container">
        <SectionTitle>RESEARCH</SectionTitle>
        <CardsContainer>
          {researchData.map((item, index) => (
            <Card key={index}>
              <IconWrapper>
                <FontAwesomeIcon icon={item.icon} />
              </IconWrapper>
              <CardTitle>{item.title}</CardTitle>
              <CardText>{item.text}</CardText>
            </Card>
          ))}
        </CardsContainer>
      </div>
    </ResearchSection>
  );
}

export default Research;
